	const float R1 = 50.0f, R2 = 30.0f;
	float t = 2 * 3.14159* EventManager::GetRandomFloat(0.0f, 1.0f);
	float q = EventManager::GetRandomFloat(0.0f, 1.0f) + EventManager::GetRandomFloat(0.0f, 1.0f);
	float r;
	float r2 = R1 / R2;
	(q > 1) ? (r = 2 - q) : (r = q);
	(r < r2) ? (r = r2 + r * ((R1 - R2) / R2)) : (r = r);
	mPosition = vec3(20.0f*r*cos(t), EventManager::GetRandomFloat(-3.0f, 3.0f), 20.0f* r*sin(t));

	float scaling = EventManager::GetRandomFloat(0.2f, 0.6f); //Can adjust value of desired scaling
	mScaling = vec3(scaling, scaling, scaling);
	//TextureMapping: using the seconadary buffer with the coresponding uv calculation
	std::vector<Vertex2> vertexBuffer2;
	numOfVertices = sizeof(vertexBuffer) / sizeof(Vertex);
	float u;
	float v;
	vec3 p;
	vec3 minimum = vec3(0, 0, 0);
	vec3 maximum = vec3(0, 0, 0);
	for (int i = 0; i < numOfVertices; i++)
	{
		minimum = vec3(min(vertexBuffer[i].position.x, minimum.x), min(vertexBuffer[i].position.y, minimum.y),
			min(vertexBuffer[i].position.z, minimum.z));
		maximum = vec3(max(vertexBuffer[i].position.x, maximum.x), min(vertexBuffer[i].position.y, maximum.y),
			min(vertexBuffer[i].position.z, maximum.z));

		p = normalize(vertexBuffer[i].position);
		u = 0.5f + atan2(-p.z, -p.x) / (2 * M_PI);
		v = 0.5f - asin(-p.y) / (M_PI);
		vec2 uv = vec2(u, v);
		Vertex2 v2 = { p, vertexBuffer[i].normal, vertexBuffer[i].color, uv };
		vertexBuffer2.push_back(v2);
	}
	boundingSphereRadius = distance(minimum, maximum) / 2;
	//HightMap: calculating the reandom hight map
	for (int i = 1; i < numOfVertices; i++)
	{
		if (tf[i])
		{
			vertexBuffer2[i].position += normalize(vertexBuffer2[i].normal)*random2;
			//vertexBuffer2[i].UV += vertexBuffer2[i].UV;
			for (int j = 0; j < numOfVertices; j++)
				if (vertexBuffer[i].position.x == vertexBuffer[j].position.x && vertexBuffer[i].position.y == vertexBuffer[j].position.y && vertexBuffer[i].position.z == vertexBuffer[j].position.z)
				{
					if (j != i)
					{
						vertexBuffer2[j].position += normalize(vertexBuffer2[j].normal)*random2;
						//vertexBuffer2[j].UV += vertexBuffer2[i].UV;
					}
				}
		}
	}

	glGenVertexArrays(1, &mVAO);
	glBindVertexArray(mVAO);

	glGenBuffers(1, &mVBO);
	glBindBuffer(GL_ARRAY_BUFFER, mVBO);
	glBufferData(GL_ARRAY_BUFFER, sizeof(Vertex2)*vertexBuffer2.size(), vertexBuffer2.data(), GL_STATIC_DRAW);

	// 1st attribute buffer : vertex Positions
	glVertexAttribPointer(0,              // attribute. No particular reason for 0, but must match the layout in the shader.
		3,              // size
		GL_FLOAT,       // type
		GL_FALSE,       // normalized?
		sizeof(Vertex2), // stride
		(void*)0        // array buffer offset
	);
	glEnableVertexAttribArray(0);

	// 2nd attribute buffer : vertex normal
	glVertexAttribPointer(1,
		3,
		GL_FLOAT,
		GL_FALSE,
		sizeof(Vertex2),
		(void*)sizeof(vec3)    // Normal is Offseted by vec3 (see class Vertex)
	);
	glEnableVertexAttribArray(1);


	// 3rd attribute buffer : vertex color
	glVertexAttribPointer(2,
		3,
		GL_FLOAT,
		GL_FALSE,
		sizeof(Vertex2),
		(void*)(2 * sizeof(vec3)) // Color is Offseted by 2 vec3 (see class Vertex)
	);
	glEnableVertexAttribArray(2);

	//4th attribute buffer : UV
	glVertexAttribPointer(3,
		2,
		GL_FLOAT,
		GL_FALSE,
		sizeof(Vertex2),
		(void*)(3 * sizeof(vec3)) // Color is Offseted by 2 vec3 (see class Vertex)
	);
	glEnableVertexAttribArray(3);

